settings.custom_css:
selector {
    animation: destacando 0.5s;
    animation-iteration-count: infinite;
}

@keyframes destacando {
    0% {
        opacity: 0;
    } 50% {
        opacity: 1;
    } 100% {
        opacity: 0;
    }
}

selector div div a {
    background-image: linear-gradient(
0deg, #D62F65 0%, #FF5C91 100%);
box-shadow: 0px 0px 25px 5px #FF5C91;
animation: batimentos 3s;
animation-iteration-count: infinite;
}

selector div div a:hover {
    background-image: linear-gradient(
180deg, #D62F65 0%, #FF5C91 100%);
box-shadow: 0px 0px 0px 0px #FF5C91;
transform: scale(0.9);
}

@keyframes batimentos {
    0% {
        transform: translateX(0px);
    }

    20% {
        transform: translateX(0px);
    }

    40% {
        transform: translateX(0px);
    }

    45% {
        transform: translateX(10px);
    }

    50% {
        transform: translateX(-10px);
    }

    55% {
        transform: translateX(10px);
    }

    60% {
        transform: translateX(-10px);
    }

    65% {
        transform: translateX(0px);
    }

    80% {
        transform: translateX(0px);
    }

    100% {
        transform: translateX(0px);
    }
}

